mapper.select:
select article_no, user_id, title, content, hit, regtime, moditime, thumbnail, (select count(*) from like where like_type = 0 and article_no = no) as like
from article
where article_no = #{article_no}

mapper.update:
update article
set hit = hit + 1
where article_no = #{article_no}

update article
set title = #{title}, content = #{content}
where article_no = #{article_no}
