mapper.update:
UPDATE chatroom
SET croom_name = #{croom_name}
WHERE croom_no = #{croom_no}

mapper.select:
SELECT COUNT(*)
FROM participant
WHERE croom_no = #{croom_no}

SELECT max(chat_no)
FROM chat
WHERE croom_no = #{croom_no}

mapper.update:
UPDATE chatroom
SET last_chat = #{message}
WHERE croom_no = #{croom_no}

UPDATE chat
SET not_read = IF(not_read > 0, not_read - 1, 0)
WHERE chat_no = #{chat_no}

mapper.select:
SELECT participant_no, user_id, croom_no, croom_name, not_read_chat, last_read_chat_id, regtime
FROM participant
WHERE user_id = #{user_id} and croom_no = #{croom_no}

update.foreach:
UPDATE participant
SET not_read_chat = (SELECT count(*) FROM chat WHERE croom_no = #{item.croom_no} AND chat_no > #{item.last_read_chat_id})
WHERE user_id = #{item.user_id} AND croom_no = #{item.croom_no}
